svg.script:

var tempColor;
function hoverPath(evt){
obj = evt.target;
tempColor = obj.getAttribute("fill");
obj.setAttribute("fill","red");
//alert(tempColor);
//obj.setAttribute("stroke","red");}
function recoverPath(evt){
obj = evt.target;
obj.setAttribute("fill", tempColor);
//obj.setAttribute("stroke", tempColor);
}
